INSERT INTO `forum_post` VALUES (53, 1, 0, '', '东莞之行有感！', '<p>1：企业做到一定的程度是否要进行拆分吗？（企业如何能长久的秩序发展）</p><p>2：企业发展的瓶颈自身如和突破？</p><p>3：大而不强的公司和强而不大的公司结局会怎样？</p><p>4：企业（公司）是否有生命周期？</p><p>5：下一个经销商的增长点（机会）在什么地方？</p><p>看见的2个老客户的现状有感悟！希望大家兄弟伙来些看法！<br /></p>', 46, 1, 648, 0, 0, 1388581941);
