INSERT INTO `forum_post` VALUES (93, 1, 91, '', '', '<p><span style=\"font-size:24px;\">13年的合同期限是12月31日截止，返点是按照合同总任务进行返点。</span><br /></p><p><span style=\"font-size:24px;\">14年的合同期限是3月31日截止，返点是按照合同每季度分解。</span></p><p><span style=\"font-size:24px;\">客户在订货方面改变了原来的年底看货订货，导致1月份不仅有春节备货，还有库存消耗的操作方式。</span></p><p><span style=\"font-size:24px;\">个人认为，14年1月份的销售额，如果按同比方式计算，与13年同比持平，也说明市场同比是有增长的。</span></p>', 0, 1, 291, 0, 0, 1389844615);
