INSERT INTO `forum_post` VALUES (2696, 1, 0, '', '分享：2016年2月14日内训销售总结', '<p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">朱茂林</span></strong><strong><span style=\"font-size:19px;font-family:宋体\">：</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">与经销商打交道要了解经销商喜好！</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">市场与渠道都是要人做出来！</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">库存管理要了解季节性压货！三个字：对</span></strong><strong><span style=\"font-size:16px\">.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">借</span></strong><strong><span style=\"font-size:16px\">.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">先</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">陈明胜</span></strong><strong><span style=\"font-size:19px;font-family:宋体\">：</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">销量产生需要适当的压货；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">对客户一定要学会算账，计算财务账，配合江湖招数就更好；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">包括县城在内，网点（铺市率）数量决定了销量以及自身在经销商心里的地位。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">刘伟强</span></strong><strong><span style=\"font-size:19px;font-family:宋体\">：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">学会去发现经销商的立场和角度；</span></strong></p><p style=\"margin-left:78px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">通过一个问题去多个当面的想方法，既要有不好的，也要分析为什么出现好的结果反向去准备；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">铺市，任何一个在小的农贸地摊都有销量。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">李侯双：</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">从问题反向思考，得出解决办法。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">从经销商看中的利益出发相处对应话术和方法。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">抓住季节性压货时间，节奏。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">杨肖：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">与经销商的互动！</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">怎样从各个方面了解经销商以及经销商人员；</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">网点开发与各种季节性机会性压货方式，数量决定“质量”。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">万健：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">一件事正面和反面，不管是什么事都不列外。</span></strong></p><p style=\"margin-left:78px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">经销商最看重的是什么？利益，切入点，为客户利益，名气，荣誉。合作是企业与企业合作等原则，来工作而非交友。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">一切皆可用，有可用的全用上，用了才知道有用与否。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">张锟：</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">旺季提前压货，抢占市场；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">与经销商人员互动，行程对川南的习惯销售；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">多角度思考，如，经销商，公司，市场超市这三个角度。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">黎仕全</span></strong><strong><span style=\"font-size:19px;color:red\">:</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">与经销商沟通要以利益为切入点；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">自己不懂可以像领导同事百度等请教；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">要经常和经销商团队进行互动，交流。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">李天纲：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">压货的时机选择及方法。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">人</span></strong><strong><span style=\"font-size:16px\">-</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">渠道的辩证关系。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3:</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">经销商关系处理与自身角色扮演。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">黄凯：</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">所有事情都要正反两面分析</span></strong><strong><span style=\"font-size:16px\"> </span></strong></p><p style=\"text-indent:47px\"><strong><span style=\"font-size:16px\">&nbsp;2.</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">一切都要从经销商的利益出发分析问题</span></strong><strong><span style=\"font-size:16px\">&nbsp; </span></strong></p><p style=\"text-indent:47px\"><strong><span style=\"font-size:16px\">&nbsp;3.</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">亮点不一定来自大店。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">汪凡钞：</span></strong><strong><span style=\"font-size:16px\">1.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">调动的前提是带动，带动的前提是互动；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">一件事情都要从正反两面去看，才能有更多的方法去应对；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3.</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">压货是销售过程中必须有的手段。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">周扬程：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、好门店选出需要老板或职业经理人现场检查。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、不要放大自己，合作就是企业之间的合作，平等公平。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、暗合经销商认知，逻辑。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">黎詹方：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、要让经销商接受，首先要学会自我管理；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商最看重的，也是能够长远发展的，一个是利益，一个是名；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、一切皆可为我所用，只有用了才知道是否有用。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">罗超：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、上去或出去，时刻保持自信和坚持不懈的精神。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、难的不是问题，而是你肯不肯想</span></strong><strong><span style=\"font-size:16px\">N</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">种解决办法。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、</span></strong><strong> </strong><strong><span style=\"font-size:16px;font-family: 宋体\">实现突破，速度，是产品在生态中存在发展的根本。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">王旭</span></strong><strong><span style=\"font-size:19px;color:red\">:&nbsp;&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、看待一件事情，两个方面，三个问题，</span></strong><strong><span style=\"font-size:16px\">n</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">个方法；</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商最注重的就是利益，和客户谈事情以利益点为切入点；</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、抓住</span></strong><strong><span style=\"font-size:16px\">2-4</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">月市场节奏重点压货！</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">慎凯：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、一件事两个面三个问题思考循环分析。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、了解经销商从喜好，特长，能力，习惯，缺点，家庭，个性，品格。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商看重利益，我们要互动，带动，调动。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">王炜：</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商打交道要做到第一，不断给予其第一次，走感动路线，与经销商互动起来。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、在客户开发方面，没有数量，哪来质量。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、县级市场操作，压货，铺市，特陈，推广。</span></strong></p><p style=\"margin-left:92px\"><strong><span style=\"font-size:19px;font-family:宋体;color:red\">倪文杰：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">、了解经销商公司以及市场的基本情况，掌握经销商公司关键人物的喜好、特长、能力，习惯，优缺点等。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商最看重的是利益，工作切入点为客户利益。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、把握好压货季节和节奏，并把握好压货方式方法。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">严礼志</span></strong><strong><span style=\"font-size:16px\">:1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、注意在与经销商沟通中的自我行为管理，多向前辈借知识。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、多与经销商互动，有互动才能了解，熟悉。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、可以通过经销商业务员或者商超门店老板向经销商压货。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">毛俊科：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、学会自我管理，做好自己，自然能使人信服；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、客户量是销量的必然条件，所以多开客户才是王道；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、要把区县做好做强，把握重点店。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">徐涛</span></strong><strong><span style=\"font-size:19px;color:red\">: &nbsp;</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、了解经销商的性格，喜好，做到有共同语言；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、到经销商前，应该做好计多参加经销商活动，多些互动；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、学会自我管理，发挥自己的优势。</span></strong></p><p style=\"margin-left:101px\"><strong><span style=\"font-size:19px;font-family:宋体;color:red\">宋学进：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">、了解经销商公司、老板及经理，学会如何和经销商打交道，学会让经销商接受自己，做好自我的管理；</span></strong></p><p style=\"margin-left:86px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商最看重的是利益和名，多和经销商互动，可以用开发客户数量不足、差异化不明显和资源分散切入；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、区县的一些压货方法和渠道。</span></strong></p><p><strong><span style=\"font-size:19px;color:red\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">梁鹏：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、件事正反两面思考；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、懂得发现自己、学会调整自己；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、多互动交流</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">杨睿：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、需要调整好自己，做好自我管理，才能得到经销商认可。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、形成差异化模式。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、面对一件事先考虑两面性，把问题细化思考。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">张旭华</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、要想管理好经销商，首先要了解经销商投其所好</span></strong><strong> </strong><strong><span style=\"font-size:16px;font-family: 宋体\">；</span></strong><strong><span style=\"font-size:16px\"> </span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商的合作是企业与企业的合作而非个人</span></strong><strong> </strong><strong><span style=\"font-size:16px;font-family: 宋体\">；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、压货一定要把握好时间，</span></strong><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">—</span></strong><strong><span style=\"font-size:16px\">4</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">月是最佳压货时间</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">刘文阳</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、对、借、先；</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、每一件事情都有正反两面，矛盾的事情才是正确的。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商沟通交流中，参与到互动中，熟悉并了解。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">王航：</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、多换位思考（“对”的事）；</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、对方利益是商业合作首要思考到的。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、重视乡镇市场的覆盖，尤其是二批的重视。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">王瑞：</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、如何了解经销商公司及沟通时注意事项；</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商</span></strong><strong> </strong><strong><span style=\"font-size:16px;font-family: 宋体\">渠道</span></strong><strong> </strong><strong><span style=\"font-size:16px;font-family: 宋体\">终端压货方法；</span></strong><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">～</span></strong><strong><span style=\"font-size:16px\">4</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">月压货期。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商人员平时的互动，关注和赞美。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">胡伟</span></strong><strong><span style=\"font-size:19px;color:red\">:&nbsp;&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、了解经销商，知己知彼，百战不殆。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、认识到与经销商的合作是公司与公司的合作，平等原则，能减轻一些不必要的包袱。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、县客户下单和压货的一些方法。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">马勇：</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、管理别人之前，先学会自我管理</span></strong><strong><span style=\"font-size:16px\">;</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商谈计划首先以经销商利益为出发点</span></strong><strong><span style=\"font-size:16px\">;</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、了解并掌握经销商网点，全面提高县城及乡镇铺市率</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">任云</span></strong><strong><span style=\"font-size:19px;color:red\">:&nbsp;&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、一事，二面，三方向，</span></strong><strong><span style=\"font-size:16px\">n</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">方法</span></strong><strong><span style=\"font-size:19px;color:red\">;</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、先利益，后谈事</span></strong><strong><span style=\"font-size:19px;color:red\">;</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、统计数据，逐步分解到终端</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">慎文才</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、了解经销商相关资料，先学会自我管理，带动经销商与经销商互动。</span></strong></p><p style=\"text-indent:63px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、经销商不太懂的，自己又不会的，积极请教老前辈，查阅各种资料。</span></strong></p><p style=\"text-indent:62px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、销量需要开客户，提高铺市率，以及抓住重点门店做特陈树立品牌形象来提升。</span></strong></p><p style=\"margin-left:83px\"><strong><span style=\"font-size:19px;font-family:宋体;color:red\">张波</span></strong><strong><span style=\"font-size:19px;color:red\">:&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">、每做一件事情都会遇到各种各样的问题，但办法总比问题多，自己想不出来就到终端去，向同事向领导请教。</span></strong></p><p style=\"margin-left:86px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、在客户心中留下一个好的映象，让客户接受我们，首先要学会自我管理自我调控，多了解客户的公司、市场以及老板本人或职业经理人及其业务员。</span></strong></p><p style=\"margin-left:86px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、压货的最终目的是压到消费者手中，第一要掌握好压货的时间、节奏，第二要跟踪货是否压向了直面消费者的终端。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">丁荣川：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、享受每一次小成功的喜悦，给予自己自信。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与客户的互动，消除客户的戒备心。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、明确自己在与客户之间所扮演的角色。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">殷学进：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、一件事二面三个问题多种方发。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、合作是企业与企业合作等原则，来工作而非交友。</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、季节压货，把握机会！</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">黎俊：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、一件事要多想为什么，成或者不成都有原因；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、压货不只是增加销量，还要打击竞品；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商沟通</span></strong><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">套路，从渠道到特陈到聚焦。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">张山鉴</span></strong><strong> </strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、对于铺市压货一定要注意季节性产品！</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、要做出亮点不断去复制！</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、合作是企业与企业之间的平等合作关系！而非交友关系！</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">马洪均：</span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、打入客户内部，抓重点业务；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、销量突破在于增加网点；</span></strong></p><p style=\"text-indent:79px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、把产品卖点转换买点在于接地气。</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体;color:red\">邹兵：</span></strong><strong><span style=\"font-size:19px;color:red\">&nbsp; </span></strong><strong><span style=\"font-size:16px\">1</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、多角度思考问题，</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、与经销商业务员互动。</span></strong></p><p style=\"text-indent:71px\"><strong><span style=\"font-size:16px\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、季节性压货。</span></strong></p><p><br/></p>', 41, 1, 278, 0, 0, 1455787353);
