INSERT INTO `forum_post` VALUES (470, 1, 466, '', '', '<p>1月5日吴芝松每日总结：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">在出现问题欲求改善时 一定要让经销商落实具体的时间和改善情况的反馈。</span></p>', 0, 1, 1413, 0, 0, 1420508380);
INSERT INTO `forum_post` VALUES (471, 1, 455, '', '', '<p>1月5日张山鉴每日总结：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">凡事都得落实在行动上，不能光说不干，这样才能发现经销商所存在的一些问题并加以解决。</span></p>', 0, 1, 1413, 0, 0, 1420508465);
INSERT INTO `forum_post` VALUES (472, 1, 467, '', '', '<p>1月5日陈晓明每日总结：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">初步接触市场更多的是了解经销商，通过市场了解本质，通过经销商了结现象。</span></p>', 0, 1, 1413, 0, 0, 1420508517);
