INSERT INTO `forum_post` VALUES (480, 1, 461, '', '', '<p>1月5日王炜每日总结：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">客户介绍客户，别的厂家介绍客户，成功率高。</span></p>', 0, 1, 1413, 0, 0, 1420510706);
