INSERT INTO `forum_post` VALUES (2491, 1, 460, '', '', '<p><span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">8月17日：事情要当面说，而且要明说，一件事一件事的单独处理，<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">县城分销商不需要大，但需要实际做事的。</span></span></p>', 0, 1, 1413, 0, 0, 1439866249);
INSERT INTO `forum_post` VALUES (771, 1, 764, '', '', '<p>0118 &nbsp;<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">1.任何事情要有计划，不打无准备的仗2.计划要执行，落地3复核，计划的可实施性，及时纠正</span></p>', 0, 1, 278, 0, 0, 1421550456);
INSERT INTO `forum_post` VALUES (773, 1, 265, '', '', '<p>1月17日 总结:&nbsp;<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">1、由于惯性思维，早上去的车站没有到铜川的车，转到北站上车，浪费时间；2、客户信息不充分，到铜川后没有找到客户；3、走访铜川市场，大卖场及当地连锁天天壹加壹超市，只有一家还在卖货，日期是14年7月货。通过询问了解，该店进货都是店员负责整理小菜的抄单打电话定，叫铜缘的供货，已经有几个月没有联系过了</span></p>', 0, 1, 278, 278, 1421551684, 1421550746);
INSERT INTO `forum_post` VALUES (774, 1, 764, '', '', '<p>0117&nbsp;沟通复述，沟通时要有互动。才可以产生有效的工作效率。</p>', 0, 1, 278, 0, 0, 1421550863);
INSERT INTO `forum_post` VALUES (775, 1, 584, '', '', '<p>1月18日与超市老板谈判时要结合店内情况，谈一些我们能给超市节省的位置，能够将以前空置的地方产生利益，再加上季节转换的需求等来度化客户的思维，从而达成销售目的</p>', 0, 1, 278, 0, 0, 1421551182);
INSERT INTO `forum_post` VALUES (776, 1, 260, '', '', '<p>1月17日&nbsp;<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">在试吃活动中要不停的聚集人气，只有围个几个人的时候，其他人才会过来看，实在没人的时候，要主动出击，把试吃品塞到消费者手里，还要适当的调节好节奏，才能保证一天的工作效率。</span></p>', 0, 1, 278, 0, 0, 1421551307);
