INSERT INTO `forum_post` VALUES (1013, 1, 762, '', '', '<p>1月26日当与经销商业务员走访市场时，发现问题立即带头与业务员一起解决，并告知经销商老板，使其引起足够重视</p>', 0, 1, 278, 0, 0, 1422490280);
