INSERT INTO `forum_post` VALUES (1588, 1, 453, '', '', '<p>区域工作需要把优势点复制工作做好，多做管理层的工作，开展项目工作，进行定期的监督（数据),；薄弱点的工作多花精力示范、培养典型人员，塑造信心！</p>', 0, 1, 773, 0, 0, 1428336466);
