INSERT INTO `forum_post` VALUES (2006, 1, 454, '', '', '<p>5月20日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">抓紧有效时间的工作。</span></p>', 0, 1, 1413, 0, 0, 1432176982);
INSERT INTO `forum_post` VALUES (2007, 1, 260, '', '', '<p>5月20日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">如何快速的取得经销商的好感和信任：第一是要勤快，让他看到你的努力，光这点还不够；第二是去发掘市场上的问题，并且带着解决方案去找经销商。</span></p>', 0, 1, 1413, 0, 0, 1432177038);
INSERT INTO `forum_post` VALUES (2008, 1, 265, '', '', '<p>5月20日：1、认知：对方业务一直做大流通批发，没有BC操作意识，只管发货，不理排面；2、到店抄单，只看价格签，摆放位置不集中，当面带头集中产品。</p>', 0, 1, 1413, 0, 0, 1432177510);
