INSERT INTO `forum_post` VALUES (2035, 1, 2009, '', '', '<p>5月21日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">特陈需要进一步完善，要有足够的视觉冲击。</span></p>', 0, 1, 1413, 0, 0, 1432260444);
INSERT INTO `forum_post` VALUES (2036, 1, 454, '', '', '<p>5月21日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">开客户之前要把所有能准备的资料、视频都准备好，包括自己的心态都要好好管理一下。</span></p>', 0, 1, 1413, 0, 0, 1432260505);
INSERT INTO `forum_post` VALUES (2037, 1, 466, '', '', '<p>5月21日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">谈客户该硬的时候一定要硬 原则上的东西坚持。</span></p>', 0, 1, 1413, 0, 0, 1432260550);
INSERT INTO `forum_post` VALUES (2038, 1, 462, '', '', '<p>5月21日：做好数据分析，<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">了解市场与客户谈判才不会被动。</span></p>', 0, 1, 1413, 0, 0, 1432260611);
