INSERT INTO `forum_post` VALUES (2042, 1, 1963, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">这次试吃有很多不足，相信经销商业务团队也一定有多感受，所以明日会针对这次试吃与业务员沟通大家谈谈。</span></p>', 0, 1, 1413, 0, 0, 1432516899);
