INSERT INTO `forum_post` VALUES (2046, 1, 762, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">今天与丹尼斯长江店与经销商业务团队做试吃推广，不管经销商业务员状态如何，首先我们要起带头作用，且让其看到效果。</span></p>', 0, 1, 1413, 0, 0, 1432517133);
INSERT INTO `forum_post` VALUES (2047, 1, 451, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">推广的品尝与叫卖，需要流动的，哪里有人就往哪里走。&nbsp;</span></p>', 0, 1, 1413, 0, 0, 1432517183);
INSERT INTO `forum_post` VALUES (2048, 1, 464, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">新产品后期铺市要跟进。</span></p>', 0, 1, 1413, 0, 0, 1432517234);
INSERT INTO `forum_post` VALUES (2049, 1, 1630, '', '', '<p>5月24日：多与顾客交谈可以提升与陌生人交谈沟通的能力。</p>', 0, 1, 1413, 0, 0, 1432517606);
INSERT INTO `forum_post` VALUES (2050, 1, 1977, '', '', '<p>5月24日：与经销商沟通在做卖场和超市瓶装菜地堆特陈的同时也必须做特价销售！这样才能更好地去吸引消费者的眼球和促动消费者来购买！</p>', 0, 1, 1413, 0, 0, 1432517942);
INSERT INTO `forum_post` VALUES (2051, 1, 260, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">把老板拉出来走店有很多好处，让他看到终端情况，给他讲一些陈列案列，拉近距离，以后工作好做。</span></p>', 0, 1, 1413, 0, 0, 1432518039);
INSERT INTO `forum_post` VALUES (2052, 1, 764, '', '', '<p>5月24日：事不隔夜，每天晚上睡觉前，想想今天定工作做完没。</p>', 0, 1, 1413, 0, 0, 1432518152);
INSERT INTO `forum_post` VALUES (2053, 1, 584, '', '', '<p>5月24日：做事要有自己的计划，带着目的性去做事情。</p>', 0, 1, 1413, 0, 0, 1432518238);
INSERT INTO `forum_post` VALUES (2054, 1, 459, '', '', '<p>5月24日：<span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑, tahoma, Arial, Verdana, Helvetica, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\">陈列只是前奏，主要还得靠勤快的维护。</span></p>', 0, 1, 1413, 0, 0, 1432518277);
INSERT INTO `forum_post` VALUES (2534, 1, 453, '', '', '<p>不但要随时调整自己的状态，坚持原则，养成良好的生活习惯和工作规律，而且要有创新的精神，敢于挑战自己（要么做好细节，每一天在进步，要么颠覆），因为创新和颠覆是改变，是一个人进步的源泉。<br/></p>', 0, 1, 773, 0, 0, 1442167498);
INSERT INTO `forum_post` VALUES (2056, 1, 449, '', '', '<p>5月24日：和业务员一起开展工作和谈判，效果会更好，不要把业务员推到前面自己藏在身后。</p>', 0, 1, 1413, 0, 0, 1432518494);
INSERT INTO `forum_post` VALUES (2057, 1, 450, '', '', '<p>5月24日总结无<br/></p>', 0, 1, 1413, 0, 0, 1432518825);
