INSERT INTO `forum_post` VALUES (2362, 1, 453, '', '', '<p>状态来自于不断的去做，去创新，去专注。等到你有了自己的计划和目标并且专注的时候，状态就在其中，之后就是提升。（无意识的实践是一种专注，是最快的提升方式）<br/></p>', 0, 1, 773, 0, 0, 1434124172);
INSERT INTO `forum_post` VALUES (2363, 1, 453, '', '', '<p>重要的项目工作必须亲自参与，定期跟踪。</p>', 0, 1, 773, 0, 0, 1434211299);
