INSERT INTO `forum_post` VALUES (2365, 1, 453, '', '', '<p>专注度的锻炼。1、看书；2、控制在某个时间段做好一件事；3、学习一些锻炼专注的书籍和游戏<br/></p>', 0, 1, 773, 0, 0, 1434383429);
