INSERT INTO `forum_post` VALUES (2693, 1, 453, '', '', '<p>温故而知新<br/></p>', 0, 1, 773, 0, 0, 1449931559);
INSERT INTO `forum_post` VALUES (2694, 1, 453, '', '', '<p>保留自己的个性和创新能力，但要遵守游戏的规则。<br/></p><p>当感觉在自我麻醉的时候，要努力去实践。当感觉在困难重重，想逃避的时候，迎难而上往往可以让自己受益匪浅。</p>', 0, 1, 773, 0, 0, 1450016967);
INSERT INTO `forum_post` VALUES (2695, 1, 453, '', '', '<p>学习的东西只有去实践验证了才知道有多少适合自己，而光记录和思考其实用处不大。学习是要学老师思考问题的思维和一些知识点（这个知识点最好还是自己平时去思考了，但不是很完全的）。</p>', 0, 1, 773, 0, 0, 1450107424);
INSERT INTO `forum_post` VALUES (2697, 1, 2696, '', '', '<p>学习借鉴<br/></p>', 0, 1, 630, 0, 0, 1455800803);
